import React, { useState } from "react";
import { Grid, Form, Button } from "semantic-ui-react";
import PublishIcon from "@material-ui/icons/Publish";
import Card from "react-bootstrap/Card";
import Avatar from "@material-ui/core/Avatar";
import CardHeader from "@material-ui/core/CardHeader";

function PostForm() {
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(false);
  const [post, setPost] = useState({
    id: 0,
    name: "",
    title: "",
    text: "",
  });

  function handleTextChange(event) {
    const { value, name } = event.target;

    if (post.title !== "" && post.text !== "") {
      //setError(false);
    }
    setPost((prevValue) => {
      if (name === "text") {
        return {
          id: prevValue.id,
          name: prevValue.name,
          title: prevValue.title,
          text: value,
        };
      } else if (name === "title") {
        return {
          id: prevValue.id,
          name: prevValue.name,
          title: value,
          text: prevValue.text,
        };
      }
    });
  }

  function onSubmit(event) {
    event.preventDefault();
    setSubmitted(true);
  }

  return (
    <div className="center" style={{ marginTop: "1%" }}>
      <Grid style={{ height: "100%" }}>
        <Grid.Column>
          <Card style={{ width: "75vw", maxWidth: "1000px", height: "100%" }}>
            <Form size="large" onSubmit={onSubmit} className="ui form success">
              <CardHeader
                avatar={<Avatar aria-label="recipe">R</Avatar>}
                titleTypographyProps={{ variant: "subtitle2" }}
                // title={props.name}
                title="作者"
                subheader="September 14, 2016"
              />
              {submitted && (
                <div className="ui success message">
                  <div className="header">Post Created</div>
                </div>
              )}
              <div className="form-group">
                <Form.Input
                  error={
                    error && {
                      content: "请输入你的标题",
                      pointing: "below",
                    }
                  }
                  type="text"
                  id="title"
                  name="title"
                  placeholder="标题"
                  required
                  onChange={handleTextChange}
                />
              </div>
              <div className="form-group">
                <Form.TextArea
                  placeholder="请输入内容"
                  name="text"
                  style={{ minHeight: "30rem" }}
                  rowsMin={30}
                  onChange={handleTextChange}
                />
              </div>

              <Button primary className="ui submit button">
                发布
              </Button>
            </Form>
          </Card>
        </Grid.Column>
      </Grid>
    </div>
  );
}

export default PostForm;
