import React from 'react';
import {Form,Image,TextArea,Button,Comment} from "semantic-ui-react";

function Add(){
    return(
        <div className="col-md-12">
          <Form>
              <br styles="clear:both" />

                  <Form.Input type="text" className="form-control" id="title" name="title" placeholder="Title" required />

                <Form.TextArea rowsMin={6}/>


           <Button>Add Post</Button>

              </Form>
        </div>
    )
}

export default Add;
