import React,{useState} from "react";
import Box from "@material-ui/core/Box"
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import FormGroup from '@material-ui/core/FormGroup';
import Card from '@material-ui/core/Card';
import Link from '@material-ui/core/Link'
import { makeStyles } from '@material-ui/core/styles';
import Axios from "axios";


function Login(){
  var UserisRegistered = false;
  const[isRegistered,setRegisterState]=useState(true);
  function handleClick(){
    setRegisterState(!isRegistered);
  }
  function signUp(e){
    console.log(docume)
    e.preventDefault(document.getElementById('username')?.value);
    if (isRegistered){
      console.log("Registered");
    }
    else{
      let userName = document.getElementById('username')?.value;
        let password = document.getElementById('password')?.value;
        let data = {
            userName:userName,
            password:password,
        }

    Axios({
      method: "POST",
      url: "http://localhost:5000/register",
      data,
      headers: {
        "Content-Type": "application/json"
      }
    }).then(res => {
      console.log(res.data.message);
    });
  }
  }
    const useStyles = makeStyles((theme) => ({
       root:{
        justifyContent: 'center',
        alignItems: 'center',
        height:"100%",
       },
       Button:{
        marginTop:"4%"
       },
       card:{
        width:"40%",
        margin:"auto",
        marginTop:"10%",
        padding:"auto",
        height:"50%"
       },
       title:{
        marginBottom:"5%"
       },
       loginSquare:{
           padding:"10%",
           backgroundColor:"white",
       },
       link:{
       marginTop:"5%"
       }
      }));
      const classes=useStyles();

        return (<div className={classes.root}>
           <Card className={classes.card}>
        <Container className={classes.loginSquare} maxWidth="xs">

        <FormGroup onSubmit={(e)=>{signUp(e)}}>   
            <h1 className={classes.title}>{isRegistered?"登陆易职网":"注册易职网"}</h1>
                <Grid item xs={12}>
            <TextField fullWidth type="text"  id='username' name="username" label="邮箱" required autofocus></TextField>
                </Grid>
                <Grid item xs={12}>
                    <TextField
                    fullWidth
                      label="密码"
                      name="password"
                      size="small"
                      type="password"
                      id='password'
                    />
                  </Grid>
                  <Grid item xs={12} >
                      <Button 
                      className={classes.Button}
                        color="primary"
                        variant="outlined"
                        fullWidth type="submit">{isRegistered?"登陆":"注册新账号"}</Button>
                  </Grid>
                  <Grid item xs={12} >
                  <div className={classes.link}>
    <Link  onClick={handleClick}>{isRegistered?"注册新账号":"已有账号"} </Link></div>
    </Grid>
        </FormGroup>

        </Container>
        </Card>


        </div>)
    }
    export default Login;
