import React from 'react';
import {Form,Button,Card} from "semantic-ui-react";

function Add(){
    return(
        <div className="col-md-6">
        <Card>
          <Form>

                <div className="form-group">
                  <Form.Input type="text" id="title" name="title" placeholder="Title" required />
                </div>
                <div className="form-group">
                <Form.TextArea rowsMin={6}/>
                </div>

           <Button primary>Add Post</Button>

              </Form>
              </Card>
        </div>

    )
}

export default Add;
