import React from 'react';
import {Grid,Form,Button,Card} from "semantic-ui-react";
import PublishIcon from '@material-ui/icons/Publish';

function Add(){
    return(
        <Grid>
        <Grid.Column width={3}></Grid.Column>
        <Grid.Column width={12} >
        <Card>
          <Form size="large">
          <Button fontSize="small"><PublishIcon/></Button>
                <div className="form-group">
                  <Form.Input type="text" id="title" name="title" placeholder="标题" required />
                </div>
                <div className="form-group">
                <Form.TextArea placeholder="请输入内容" rowsMin={30}/>
                </div>


           <Button primary>发布</Button>

              </Form>
              </Card>
        </Grid.Column>
        </Grid>
    )
}

export default Add;
