import React, { useState } from "react";
import { Grid, Form, Button } from "semantic-ui-react";
import PublishIcon from "@material-ui/icons/Publish";
import Card from "react-bootstrap/Card";

function Add() {
  const [submitted, setSubmitted] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [post, setPost] = useState({
    id: 0,
    name: "",
    title: "",
    text: "",
  });

  function handleTextChange(event) {
    const { value, name } = event.target;
    setPost((prevValue) => {
      if (name === "text") {
        return {
          id: prevValue.id,
          name: prevValue.name,
          title: prevValue.title,
          text: value,
        };
      } else if (name === "title") {
        return {
          id: prevValue.id,
          name: prevValue.name,
          title: value,
          text: prevValue.text,
        };
      }
    });
  }

  function onSubmit(event) {
    event.preventDefault();

    if (!isFormValid()) {
      alert("Error");
      return;
    }

    setSubmitted(true);
    alert("Submitted");
  }

  function isFormValid() {
    return post.title !== "" && post.text !== "";
  }

  return (
    <Grid style={{ height: "100%" }}>
      <Grid.Column width={3}></Grid.Column>
      <Grid.Column width={12}>
        <Card style={{ width: "50rem", height: "100%" }}>
          <Form size="large" onSubmit={onSubmit} className="ui form success">
            <Button style={{ marginBottom: "20px" }} fontSize="small">
              <PublishIcon />
            </Button>
            {!submitted && (
              <div className="ui success message">
                <div className="header">Post Created</div>
              </div>
            )}
            <div className="form-group">
              <Form.Input
                type="text"
                id="title"
                name="title"
                placeholder="标题"
                required
                onChange={handleTextChange}
              />
            </div>
            <div className="form-group">
              <Form.TextArea
                placeholder="请输入内容"
                name="text"
                style={{ minHeight: "30rem" }}
                rowsMin={30}
                onChange={handleTextChange}
              />
            </div>

            <Button primary className="ui submit button">
              发布
            </Button>
          </Form>
        </Card>
      </Grid.Column>
    </Grid>
  );
}

export default Add;
